/*
Showing image load when call Ajax
 */
$(document).ajaxStart(function(){
    $(".page-md").css("opacity", "0.3");
    $(".page-md").css("background-image", "url('../../../../../assets/img/loading2.gif')");
    $(".page-md").css("background-repeat", "no-repeat repeat");
    $(".page-md").css("background-position", "center");
});

$(document).ajaxComplete(function(){
    $(".page-md").css("opacity", "");
    $(".page-md").css("background-image", "");
    $(".page-md").css("background-repeat", "");
    $(".page-md").css("background-position", "");
});

/*
Showing dialog box with UIBootbox.
Required: assets/global/plugins/bootbox/bootbox.min.js
 */
function confirmDelete(Url,Token){
    bootbox.confirm({
        message: 'Confirma apagar o registro?',
        buttons: {
            confirm: {
                label: 'Sim',
                className: 'btn-danger',
            },
            cancel: {
                label: 'NÃ£o',
                className: 'btn-success',
            }
        },
        callback: function (result) {
            if (result) {
                $.ajax({
                   type: 'DELETE',
                    url: Url,
                    dataType: 'json',
                    headers: { 'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content') },
                    data: {"_token": Token},
                    success: function(data){
                        location.href = data.redirectTo;
                    },
                    error: function(data){
                        console.log('Error');
                    }
                });
            }
        }
    })
};